import React, { useState, useEffect, useRef } from 'react';
import { Canvas } from '@react-three/fiber';
import { Points, PointMaterial, Box } from '@react-three/drei';
import * as THREE from 'three';

const GyroscopePlot = ({ data }) => {
    const bufferRef = useRef<THREE.BufferGeometry>(null);

    useEffect(() => {
        if (bufferRef.current && data && data.length > 0) {
            const positions = new Float32Array(data.flatMap(d => [d.x, d.y, d.z]));

            // Create a new BufferAttribute with the updated data
            const positionAttribute = new THREE.BufferAttribute(positions, 3);
            bufferRef.current.setAttribute('position', positionAttribute);
            console.log("here: ", bufferRef.current)
        }
    }, [data]);

    return (
        <Canvas>
      <ambientLight intensity={0.5} />
      <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} />
      <pointLight position={[-10, -10, -10]} />
      <Box position={[-1.2, 0, 0]} />
<Box position={[2.5, 0, 0]} />

    </Canvas>
    );
};

export default GyroscopePlot;
