import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const GyroscopePlot = ({ data, scale = 0.1 }) => { // scale factor
    const mountRef = useRef(null);
    const lineRef = useRef(null);

    useEffect(() => {
        const width = 800; // Increase width as needed
        const height = 800; // Increase height as needed

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
        camera.position.set(0, 0, 50); // Move the camera further away

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(width, height);
        renderer.setClearColor(0xffffff, 1); 
        mountRef.current.appendChild(renderer.domElement);

        // Enlarge the GridHelper
        const gridHelper = new THREE.GridHelper(50, 50); // Increase the grid size
        scene.add(gridHelper);
    

        
        // GridHelper for Y and Z axes, rotated to align with these axes
    const gridHelperYZ = new THREE.GridHelper(50, 50);
    gridHelperYZ.rotation.x = Math.PI / 2; // Rotate 90 degrees around X
    scene.add(gridHelperYZ);

    // GridHelper for X and Y axes, rotated to align with these axes
    const gridHelperXY = new THREE.GridHelper(50, 50);
    gridHelperXY.rotation.z = Math.PI / 2; // Rotate 90 degrees around Z
    scene.add(gridHelperXY);

        // Create an initial empty line
        const geometry = new THREE.BufferGeometry();
        const material = new THREE.LineBasicMaterial({ color: 0x0000ff });
        lineRef.current = new THREE.Line(geometry, material);
        scene.add(lineRef.current);

                // Add red line for X-axis centerline
const materialRedX = new THREE.LineBasicMaterial({ color: 0xff0000 });
const pointsRedX = [new THREE.Vector3(-25, 0, 0), new THREE.Vector3(25, 0, 0)];
const geometryRedX = new THREE.BufferGeometry().setFromPoints(pointsRedX);
const lineRedX = new THREE.Line(geometryRedX, materialRedX);
scene.add(lineRedX);

// Add red line for Y-axis centerline
const materialRedY = new THREE.LineBasicMaterial({ color: 0xff0000 });
const pointsRedY = [new THREE.Vector3(0, -25, 0), new THREE.Vector3(0, 25, 0)];
const geometryRedY = new THREE.BufferGeometry().setFromPoints(pointsRedY);
const lineRedY = new THREE.Line(geometryRedY, materialRedY);
scene.add(lineRedY);


        const controls = new OrbitControls(camera, renderer.domElement);

        const animate = () => {
            requestAnimationFrame(animate);
            controls.update(); 
            renderer.render(scene, camera);
        };

        animate();

        return () => {
            mountRef.current.removeChild(renderer.domElement);
        };
    }, []);

    useEffect(() => {
        if (lineRef.current) {
            const scaledPoints = data.map(d => new THREE.Vector3(d.x * scale, d.y * scale, d.z * scale));
            lineRef.current.geometry.dispose(); 
            lineRef.current.geometry = new THREE.BufferGeometry().setFromPoints(scaledPoints);
        }
    }, [data, scale]);

    return <div ref={mountRef} style={{ width: '800px', height: '800px' }} />;
};

export default GyroscopePlot;
