import React from 'react';
import { Canvas } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';
import * as THREE from 'three';

const GyroscopePlot = ({ data }) => {
    // Convert data to a Float32Array for the bufferAttribute
    const positions = data && data.length > 0 
        ? new Float32Array(data.flatMap(d => [d.x, d.y, d.z])) 
        : new Float32Array(0);

    return (
        <Canvas>
            <ambientLight intensity={0.5} />
            <pointLight position={[10, 10, 10]} />
            <Points>
                <bufferGeometry attach="geometry">
                    <bufferAttribute
                        attachObject={['attributes', 'position']}
                        array={positions}
                        count={positions.length / 3}
                        itemSize={3}
                    />
                </bufferGeometry>
                <PointMaterial color={'orange'} size={0.5} />
            </Points>
        </Canvas>
    );
};

export default GyroscopePlot;
