// Ensure the uid() function is defined in your code as you've shown earlier

const fetchChatGPTResponse = async (userMessage) => {
    try {
        const resp = await fetch("https://chat.openai.com/api/auth/session")
        const data = await resp.json()
      const accessToken = data.accessToken; // Replace with your access token
      const response = await fetch("https://chat.openai.com/backend-api/conversation", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${accessToken}`, // Replace with your access token
        },
        body: JSON.stringify({
          action: "next",
          messages: [
            {
              role: "user",
              content: userMessage,
            },
          ],
          model: "text-davinci-002-render",
          parent_message_id: uid(), // Generate a unique ID for the message
        }),
      });
  
      if (!response.ok) {
        throw new Error("Failed to fetch response from ChatGPT");
      }
  
      const responseData = await response.json();
      return responseData.choices[0].message.content;
    } catch (error) {
      console.error("Error fetching data:", error);
      throw error;
    }
  };
  
  // Example usage:
  const userMessage = "Hello, ChatGPT!";
  fetchChatGPTResponse(userMessage)
    .then((aiResponse) => {
      console.log("AI Response:", aiResponse);
    })
    .catch((error) => {
      console.error("Error:", error);
    });
