import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const GyroscopePlot = ({ data, scale = 0.1 }) => {
    const mountRef = useRef(null);
    const lineRef = useRef(null);

    useEffect(() => {
        const width = 800;
        const height = 800;

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
        camera.position.set(0, 0, 50);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(width, height);
        renderer.setClearColor(0xffffff, 1);
        mountRef.current.appendChild(renderer.domElement);

        // Grid Helper with reduced density for a MATLAB-like appearance
        const gridHelper = new THREE.GridHelper(50, 10, 0xCCCCCC, 0xCCCCCC);
        scene.add(gridHelper);

        // Grid Helpers for Y and Z axes
        const gridHelperYZ = new THREE.GridHelper(50, 10);
        gridHelperYZ.rotation.x = Math.PI / 2;
        scene.add(gridHelperYZ);

        const gridHelperXY = new THREE.GridHelper(50, 10);
        gridHelperXY.rotation.z = Math.PI / 2;
        scene.add(gridHelperXY);

        // Red lines for the center axes
        const addCenterLine = (points, color) => {
            const material = new THREE.LineBasicMaterial({ color });
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const line = new THREE.Line(geometry, material);
            scene.add(line);
        };

        addCenterLine([new THREE.Vector3(-25, 0, 0), new THREE.Vector3(25, 0, 0)], 0xff0000); // X-axis
        addCenterLine([new THREE.Vector3(0, -25, 0), new THREE.Vector3(0, 25, 0)], 0xff0000); // Y-axis

        // Initial empty line for data plotting
        const geometry = new THREE.BufferGeometry();
        const material = new THREE.LineBasicMaterial({ color: 0x0000ff });
        lineRef.current = new THREE.Line(geometry, material);
        scene.add(lineRef.current);

        // Controls
        const controls = new OrbitControls(camera, renderer.domElement);

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };

        animate();

        // Clean up
        return () => {
            mountRef.current.removeChild(renderer.domElement);
        };
    }, []);

    useEffect(() => {
        if (lineRef.current) {
            const scaledPoints = data.map(d => new THREE.Vector3(d.x * scale, d.y * scale, d.z * scale));
            lineRef.current.geometry.dispose();
            lineRef.current.geometry = new THREE.BufferGeometry().setFromPoints(scaledPoints);
        }
    }, [data, scale]);

    return <div ref={mountRef} style={{ width: '800px', height: '800px' }} />;
};

export default GyroscopePlot;
