const GyroscopePlot = ({ data }) => {
    // Create a ref to store the bufferGeometry
    const bufferRef = useRef();

    // Update the bufferGeometry when data changes
    useEffect(() => {
        if (bufferRef.current) {
            const positions = data.flatMap(d => [d.x, d.y, d.z]);
            bufferRef.current.setAttribute('position', new THREE.BufferAttribute(new Float32Array(positions), 3));
            bufferRef.current.attributes.position.needsUpdate = true;
        }
    }, [data]);

    return (
        <Canvas>
            <ambientLight intensity={0.5} />
            <pointLight position={[10, 10, 10]} />
            <Points>
                <bufferGeometry ref={bufferRef} />
                <PointMaterial color={'orange'} size={0.5} />
            </Points>
        </Canvas>
    );
};
