import React from 'react';
import { Canvas } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';

const GyroscopePlot = ({ data }) => {
    // Flatten the data array to be used in bufferAttribute
    if (!data || !Array.isArray(data)) return null; // Add this line at the beginning of GyroscopePlot

    const positions = data.flatMap(point => [point.x, point.y, point.z]);
    

    return (
        <Canvas>
            <ambientLight intensity={0.5} />
            <pointLight position={[10, 10, 10]} />
            <Points>
                <bufferGeometry attach="geometry">
                    <bufferAttribute 
                        attachObject={['attributes', 'position']}
                        count={data.length}
                        array={new Float32Array(positions)}
                        itemSize={3}
                    />
                </bufferGeometry>
                <PointMaterial color={'orange'} size={0.5} />
            </Points>
        </Canvas>
    );
};

export default GyroscopePlot;
