import React, { useState } from 'react';
import BluetoothComponent from './components/BluetoothComponent';
import AI from './components/OpenAIAPI';

const App: React.FC = () => {
  const [shouldMakeApiRequest, setShouldMakeApiRequest] = useState(false);

  // Function to trigger the API request when needed
  const handleApiRequest = () => {
    setShouldMakeApiRequest(true);
  };

  return (
    <div className='bg-gray-500'>
      <BluetoothComponent />
      <button onClick={handleApiRequest}>Trigger AI Request</button>
      {shouldMakeApiRequest && <AI />}
    </div>
  );
};

export default App;
