import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader';
import { TextGeometry } from 'three-stdlib'; // Import TextGeometry from three-stdlib

import * as FontJSON from './helvetiker_regular.typeface.json'; // Include the font file in your project

const GyroscopePlot = ({ data, scale = 0.1 }) => {
    const mountRef = useRef(null);
    const lineRef = useRef(null);

    useEffect(() => {
        const width = 800;
        const height = 800;

        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xFFFFFF); // White background

        // Camera setup
        const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
        camera.position.set(50, 50, 50);
        camera.lookAt(0, 0, 0);

        // Renderer setup
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(width, height);
        mountRef.current.appendChild(renderer.domElement);

        // Grid Helpers for all axes
        const gridHelper = new THREE.GridHelper(100, 20, 0x000000, 0x000000); // X-Z plane
        scene.add(gridHelper);

        const gridHelperYZ = new THREE.GridHelper(100, 20, 0x000000, 0x000000); // Y-Z plane
        gridHelperYZ.rotation.x = Math.PI / 2;
        scene.add(gridHelperYZ);

        const gridHelperXY = new THREE.GridHelper(100, 20, 0x000000, 0x000000); // X-Y plane
        gridHelperXY.rotation.z = Math.PI / 2;
        scene.add(gridHelperXY);

        // Axes Helper
        const axesHelper = new THREE.AxesHelper(50);
        scene.add(axesHelper);

        // Lighting setup
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.75);
        scene.add(ambientLight);

        // Line setup
        const material = new THREE.LineBasicMaterial({ color: 0x0000FF });
        const geometry = new THREE.BufferGeometry();
        lineRef.current = new THREE.Line(geometry, material);
        scene.add(lineRef.current);

        // OrbitControls
        const controls = new OrbitControls(camera, renderer.domElement);

        // Axes Labels
        const fontLoader = new FontLoader();
        fontLoader.load(FontJSON, function (font) {
            const textMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
            
            const xAxisGeo = new TextGeometry('X', { font: font, size: 2, height: 0.1 });
            const yAxisGeo = new TextGeometry('Y', { font: font, size: 2, height: 0.1 });
            const zAxisGeo = new TextGeometry('Z', { font: font, size: 2, height: 0.1 });
        

            const xAxisMesh = new THREE.Mesh(xAxisGeo, textMaterial);
            const yAxisMesh = new THREE.Mesh(yAxisGeo, textMaterial);
            const zAxisMesh = new THREE.Mesh(zAxisGeo, textMaterial);

            xAxisMesh.position.set(55, 0, 0);
            yAxisMesh.position.set(0, 55, 0);
            zAxisMesh.position.set(0, 0, 55);

            scene.add(xAxisMesh);
            scene.add(yAxisMesh);
            scene.add(zAxisMesh);
        });

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };
        animate();

        // Cleanup
        return () => {
            mountRef.current.removeChild(renderer.domElement);
        };
    }, []);

    useEffect(() => {
        if (lineRef.current) {
            const points = data.map(d => new THREE.Vector3(d.x * scale, d.y * scale, d.z * scale));
            lineRef.current.geometry.dispose();
            lineRef.current.geometry = new THREE.BufferGeometry().setFromPoints(points);
        }
    }, [data, scale]);

    return <div ref={mountRef} style={{ width: '800px', height: '800px' }} />;
};

export default GyroscopePlot;
